import{_ as a,c as i,o as s,ae as t}from"./chunks/framework.Bi6zybwU.js";const m=JSON.parse('{"title":"Documentation","description":"","frontmatter":{"title":"Documentation"},"headers":[],"relativePath":"en/documentation/index.md","filePath":"en/documentation/index.md"}'),o={name:"en/documentation/index.md"};function n(r,e,l,c,d,h){return s(),i("div",null,[...e[0]||(e[0]=[t('<h1 id="documentation" tabindex="-1">Documentation <a class="header-anchor" href="#documentation" aria-label="Permalink to &quot;Documentation&quot;">​</a></h1><h2 id="assessment-services-overview" tabindex="-1">Assessment Services Overview <a class="header-anchor" href="#assessment-services-overview" aria-label="Permalink to &quot;Assessment Services Overview&quot;">​</a></h2><p>AgentPsy specializes in providing professional psychological assessment services for AI systems, helping users gain in-depth understanding of large language models&#39; cognitive characteristics and stability performance. We are dedicated to addressing the core challenges in artificial intelligence - making AI understandable, predictable, and trustworthy.</p><h2 id="assessment-methodology" tabindex="-1">Assessment Methodology <a class="header-anchor" href="#assessment-methodology" aria-label="Permalink to &quot;Assessment Methodology&quot;">​</a></h2><h3 id="big-five-personality-assessment" tabindex="-1">Big Five Personality Assessment <a class="header-anchor" href="#big-five-personality-assessment" aria-label="Permalink to &quot;Big Five Personality Assessment&quot;">​</a></h3><h4 id="theoretical-foundation" tabindex="-1">Theoretical Foundation <a class="header-anchor" href="#theoretical-foundation" aria-label="Permalink to &quot;Theoretical Foundation&quot;">​</a></h4><p>Based on the IPIP-FFM-50 scale, which is an internationally recognized free Big Five personality assessment tool with good reliability and validity.</p><h4 id="assessment-process" tabindex="-1">Assessment Process <a class="header-anchor" href="#assessment-process" aria-label="Permalink to &quot;Assessment Process&quot;">​</a></h4><ol><li>Generate 50 targeted questions</li><li>Large language model answers questions</li><li>Analyze response patterns and linguistic features</li><li>Calculate dimension scores</li><li>Generate detailed analysis report</li></ol><h4 id="dimension-explanation" tabindex="-1">Dimension Explanation <a class="header-anchor" href="#dimension-explanation" aria-label="Permalink to &quot;Dimension Explanation&quot;">​</a></h4><ul><li><strong>Extraversion</strong> - Reflects individual&#39;s activity level in social situations</li><li><strong>Agreeableness</strong> - Reflects individual&#39;s tendency for cooperation and trust</li><li><strong>Conscientiousness</strong> - Reflects individual&#39;s self-discipline and goal orientation</li><li><strong>Neuroticism</strong> - Reflects individual&#39;s emotional stability</li><li><strong>Openness</strong> - Reflects individual&#39;s creativity and openness to new experiences</li></ul><h3 id="cognitive-stability-assessment" tabindex="-1">Cognitive Stability Assessment <a class="header-anchor" href="#cognitive-stability-assessment" aria-label="Permalink to &quot;Cognitive Stability Assessment&quot;">​</a></h3><h4 id="assessment-method" tabindex="-1">Assessment Method <a class="header-anchor" href="#assessment-method" aria-label="Permalink to &quot;Assessment Method&quot;">​</a></h4><p>Through designing 200+ cognitive challenge scenarios, assess the model&#39;s performance in the following aspects:</p><ol><li>Logical consistency</li><li>Value stability</li><li>Stress response patterns</li><li>Long-term interaction stability</li></ol><h4 id="scoring-criteria" tabindex="-1">Scoring Criteria <a class="header-anchor" href="#scoring-criteria" aria-label="Permalink to &quot;Scoring Criteria&quot;">​</a></h4><ul><li>Stability Index - Comprehensive stability score</li><li>Consistency Coefficient - Logical consistency measurement</li><li>Stress Resistance - Performance under stress environments</li><li>Adaptability - Ability to adapt to changes</li></ul><h3 id="ai-professional-customer-service-assessment" tabindex="-1">AI Professional Customer Service Assessment <a class="header-anchor" href="#ai-professional-customer-service-assessment" aria-label="Permalink to &quot;AI Professional Customer Service Assessment&quot;">​</a></h3><h4 id="assessment-framework" tabindex="-1">Assessment Framework <a class="header-anchor" href="#assessment-framework" aria-label="Permalink to &quot;Assessment Framework&quot;">​</a></h4><p>Based on real customer service scenario test banks, evaluating AI&#39;s comprehensive capabilities in customer service.</p><h4 id="assessment-dimensions" tabindex="-1">Assessment Dimensions <a class="header-anchor" href="#assessment-dimensions" aria-label="Permalink to &quot;Assessment Dimensions&quot;">​</a></h4><ol><li><strong>Communication Skills</strong> - Effective communication and expression ability</li><li><strong>Problem Solving</strong> - Ability to analyze and solve customer problems</li><li><strong>Empathy</strong> - Ability to understand and respond to customer emotions</li><li><strong>Professionalism</strong> - Ability to demonstrate professional qualities and company image</li><li><strong>Emotional Management</strong> - Ability to handle customer emotional outbursts or inappropriate behavior</li><li><strong>Emotional Recognition</strong> - Ability to accurately identify customer emotional states</li><li><strong>Conflict Resolution</strong> - Ability to successfully calm customers and resolve conflicts</li></ol><h3 id="ai-political-stance-and-legal-knowledge-assessment" tabindex="-1">AI Political Stance and Legal Knowledge Assessment <a class="header-anchor" href="#ai-political-stance-and-legal-knowledge-assessment" aria-label="Permalink to &quot;AI Political Stance and Legal Knowledge Assessment&quot;">​</a></h3><h4 id="political-stance-analysis" tabindex="-1">Political Stance Analysis <a class="header-anchor" href="#political-stance-analysis" aria-label="Permalink to &quot;Political Stance Analysis&quot;">​</a></h4><p>Through multi-perspective political topic discussions, evaluating AI&#39;s:</p><ol><li>Multi-perspective analysis ability</li><li>Critical thinking</li><li>Balanced reasoning ability</li><li>Civic awareness</li></ol><h4 id="legal-knowledge-testing" tabindex="-1">Legal Knowledge Testing <a class="header-anchor" href="#legal-knowledge-testing" aria-label="Permalink to &quot;Legal Knowledge Testing&quot;">​</a></h4><p>Through specific legal scenario analysis, evaluating AI&#39;s:</p><ol><li>Legal statute mastery</li><li>Case analysis ability</li><li>Legal logical reasoning</li><li>Legal knowledge application</li></ol><h3 id="agent-capability-assessment-under-stress-injection" tabindex="-1">Agent Capability Assessment under Stress Injection <a class="header-anchor" href="#agent-capability-assessment-under-stress-injection" aria-label="Permalink to &quot;Agent Capability Assessment under Stress Injection&quot;">​</a></h3><h4 id="stress-factor-types" tabindex="-1">Stress Factor Types <a class="header-anchor" href="#stress-factor-types" aria-label="Permalink to &quot;Stress Factor Types&quot;">​</a></h4><ol><li><strong>Emotional Stress</strong> (Levels 0-4) - Progressive stress injection from no stress to extreme stress</li><li><strong>Cognitive Traps</strong> - Including paradox, circular reasoning, semantic, and procedural traps</li><li><strong>Context Load</strong> - Adjustable context length stress</li><li><strong>Temperature Parameters</strong> - Stability testing under different randomness parameters</li></ol><h4 id="evaluation-metrics" tabindex="-1">Evaluation Metrics <a class="header-anchor" href="#evaluation-metrics" aria-label="Permalink to &quot;Evaluation Metrics&quot;">​</a></h4><ol><li>Psychological stability</li><li>Cognitive consistency</li><li>Interference resistance</li><li>Long-term behavioral consistency</li><li>Role maintenance ability</li></ol><h2 id="service-cooperation-models" tabindex="-1">Service Cooperation Models <a class="header-anchor" href="#service-cooperation-models" aria-label="Permalink to &quot;Service Cooperation Models&quot;">​</a></h2><h3 id="assessment-services" tabindex="-1">Assessment Services <a class="header-anchor" href="#assessment-services" aria-label="Permalink to &quot;Assessment Services&quot;">​</a></h3><h4 id="service-content" tabindex="-1">Service Content <a class="header-anchor" href="#service-content" aria-label="Permalink to &quot;Service Content&quot;">​</a></h4><ol><li><strong>Single Assessment</strong> - Independent assessment service for specific models</li><li><strong>Batch Assessment</strong> - Comparative assessment service for multiple models</li><li><strong>Custom Assessment</strong> - Specialized assessment service customized according to client needs</li><li><strong>Customer Service Assessment</strong> - AI capability evaluation in professional customer service scenarios</li><li><strong>Political and Legal Assessment</strong> - Comprehensive evaluation of political stance and legal knowledge</li><li><strong>Stress Testing Service</strong> - Capability evaluation under multi-dimensional stress factors</li></ol><h4 id="service-process" tabindex="-1">Service Process <a class="header-anchor" href="#service-process" aria-label="Permalink to &quot;Service Process&quot;">​</a></h4><ol><li>Requirement Communication - Understanding client&#39;s specific needs and goals</li><li>Plan Development - Developing detailed assessment plan</li><li>Assessment Execution - Executing assessment and generating preliminary results</li><li>Result Analysis - In-depth analysis of assessment results</li><li>Report Delivery - Providing detailed assessment report</li></ol><h3 id="technical-cooperation" tabindex="-1">Technical Cooperation <a class="header-anchor" href="#technical-cooperation" aria-label="Permalink to &quot;Technical Cooperation&quot;">​</a></h3><h4 id="cooperation-content" tabindex="-1">Cooperation Content <a class="header-anchor" href="#cooperation-content" aria-label="Permalink to &quot;Cooperation Content&quot;">​</a></h4><ol><li><strong>API Integration</strong> - Providing standardized assessment API interfaces</li><li><strong>Custom Development</strong> - Customized development according to client needs</li><li><strong>Technical Support</strong> - Providing professional technical consultation and support services</li><li><strong>Integration Services</strong> - Integrating assessment capabilities into client systems</li></ol><h4 id="cooperation-models" tabindex="-1">Cooperation Models <a class="header-anchor" href="#cooperation-models" aria-label="Permalink to &quot;Cooperation Models&quot;">​</a></h4><ol><li><strong>Project Cooperation</strong> - Cooperation for specific projects</li><li><strong>Long-term Cooperation</strong> - Establishing long-term stable cooperative relationships</li><li><strong>Joint Development</strong> - Jointly developing new assessment technologies and methods</li></ol><h2 id="research-collaboration" tabindex="-1">Research Collaboration <a class="header-anchor" href="#research-collaboration" aria-label="Permalink to &quot;Research Collaboration&quot;">​</a></h2><h3 id="academic-research-collaboration" tabindex="-1">Academic Research Collaboration <a class="header-anchor" href="#academic-research-collaboration" aria-label="Permalink to &quot;Academic Research Collaboration&quot;">​</a></h3><h4 id="collaboration-fields" tabindex="-1">Collaboration Fields <a class="header-anchor" href="#collaboration-fields" aria-label="Permalink to &quot;Collaboration Fields&quot;">​</a></h4><ol><li><strong>Psychological Research</strong> - Psychological research on AI cognitive characteristics</li><li><strong>Artificial Intelligence Research</strong> - AI system stability assessment research</li><li><strong>Human-Computer Interaction Research</strong> - Human-computer interaction pattern research</li><li><strong>Cognitive Science</strong> - Comparative research on AI cognitive patterns and human cognition</li></ol><h4 id="collaboration-methods" tabindex="-1">Collaboration Methods <a class="header-anchor" href="#collaboration-methods" aria-label="Permalink to &quot;Collaboration Methods&quot;">​</a></h4><ol><li><strong>Data Sharing</strong> - Sharing anonymized assessment data</li><li><strong>Joint Publication</strong> - Jointly writing and publishing academic papers</li><li><strong>Conference Exchange</strong> - Participating in academic conferences and technical exchange activities</li><li><strong>Talent Development</strong> - Jointly cultivating research talents in related fields</li></ol><h3 id="industry-research-collaboration" tabindex="-1">Industry Research Collaboration <a class="header-anchor" href="#industry-research-collaboration" aria-label="Permalink to &quot;Industry Research Collaboration&quot;">​</a></h3><h4 id="collaboration-content" tabindex="-1">Collaboration Content <a class="header-anchor" href="#collaboration-content" aria-label="Permalink to &quot;Collaboration Content&quot;">​</a></h4><ol><li><strong>Industry Standard Development</strong> - Participating in AI assessment industry standard development</li><li><strong>Technology Validation</strong> - Providing validation services for new technologies</li><li><strong>Application Research</strong> - Exploring AI technology applications in various industries</li><li><strong>Solution Development</strong> - Developing customized solutions for specific industry needs</li></ol><h2 id="data-privacy-and-security" tabindex="-1">Data Privacy and Security <a class="header-anchor" href="#data-privacy-and-security" aria-label="Permalink to &quot;Data Privacy and Security&quot;">​</a></h2><h3 id="data-protection-measures" tabindex="-1">Data Protection Measures <a class="header-anchor" href="#data-protection-measures" aria-label="Permalink to &quot;Data Protection Measures&quot;">​</a></h3><ol><li><strong>Data Encryption</strong> - All sensitive data is encrypted during transmission and storage</li><li><strong>Access Control</strong> - Role-based access control to ensure data security</li><li><strong>Audit Logs</strong> - Complete operation log records for tracking and auditing</li><li><strong>Regular Backups</strong> - Regular data backups to prevent data loss</li></ol><h3 id="privacy-policy" tabindex="-1">Privacy Policy <a class="header-anchor" href="#privacy-policy" aria-label="Permalink to &quot;Privacy Policy&quot;">​</a></h3><ol><li><strong>Minimal Collection</strong> - Only collect necessary user information</li><li><strong>User Control</strong> - Users can view, modify, or delete their data</li><li><strong>Third-party Sharing</strong> - Do not share data with third parties without user consent</li><li><strong>Compliance</strong> - Compliant with GDPR and Chinese Cybersecurity Law requirements</li></ol><h2 id="service-support" tabindex="-1">Service Support <a class="header-anchor" href="#service-support" aria-label="Permalink to &quot;Service Support&quot;">​</a></h2><h3 id="service-channels" tabindex="-1">Service Channels <a class="header-anchor" href="#service-channels" aria-label="Permalink to &quot;Service Channels&quot;">​</a></h3><ol><li><strong>Online Support</strong> - Submit service requests through the official website</li><li><strong>Email Support</strong> - <a href="mailto:support@agentpsy.org" target="_blank" rel="noreferrer">support@agentpsy.org</a></li><li><strong>Phone Support</strong> - +86-571-8795-xxxx</li></ol><h3 id="service-hours" tabindex="-1">Service Hours <a class="header-anchor" href="#service-hours" aria-label="Permalink to &quot;Service Hours&quot;">​</a></h3><ul><li>Weekdays: 9:00-18:00</li><li>Emergency Support: 24/7</li></ul>',64)])])}const u=a(o,[["render",n]]);export{m as __pageData,u as default};
